#include<iostream>
#include<string>
using namespace std;
int main()
{
	string a = "";
	int *arr= new int[a.size()];
	for(int i=0;i<a.size();i++)
	{
		arr[i]=(int)a.at(i)-48;
// 		cout<<arr[i]<<endl;
	}
	int adjacent = 13;
	long int maxim = -1;
	long int mul=1;
	for(int i=0;i+adjacent<=a.size();i++)
	{
		int j=0;
		
		if(i==0||arr[i-1+adjacent]==0)
		{
		    if(arr[i-1+adjacent]==0)
		    i+=adjacent;
		    if(i+adjacent>a.size())
		     break;
		    mul=1;
			while(j<adjacent)
			{
			    if(arr[i+j]==0)
			    {
			        
			        i+=j+1;
			        if(i+adjacent>a.size())
			         break;
			        j=0;
			        mul=1;
			    }
				mul*=arr[(i+j)];
				j++;
			}
		}
		
		else
		{
		    if(arr[i-1]!=0)
			mul = mul*arr[(i-1+adjacent)]/arr[i-1];
		}
// 		cout<<mul<<" "<<maxim<<endl;
		if(mul>maxim)
			maxim=mul;
	}
	cout<<maxim;
	return 0;
}